float hash( vec3 p )
{
	float h = dot(p,vec3(127.1,311.7,201.3));

    return fract(sin(h)*43758.5453123);
}

float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix(mix( mix( hash( i + vec3(0,0,0) ), 
                         hash( i + vec3(1,0,0) ), u.x),
                    mix( hash( i + vec3(0,1,0) ), 
                         hash( i + vec3(1,1,0) ), u.x), u.y),
               mix( mix( hash( i + vec3(0,0,1) ), 
                         hash( i + vec3(1,0,1) ), u.x),
                    mix( hash( i + vec3(0,1,1) ), 
                         hash( i + vec3(1,1,1) ), u.x), u.y), u.z );
}

float noise(vec2 p)
{
    return noise(vec3(p.xy, 1));
}

float fbm(vec2 p)
{
    const mat2 m = mat2(0.8, 0.6, -0.6, 0.8);

    float f = 0.0;
    f += 0.5000 * noise(p); p = m * p * 2.02;
    f += 0.2500 * noise(p); p = m * p * 2.03;
    f += 0.1250 * noise(p); p = m * p * 2.01;
    f += 0.0625 * noise(p);
    return f / 0.9375;
}

float dfbm(vec2 p, vec2 dp)
{
    float dx = (fbm(p + vec2(dp.x, 0.0)) - fbm(p)) / dp.x;
    float dy = (fbm(p + vec2(0.0, dp.y)) - fbm(p)) / dp.y;
    return sqrt(dx*dx + dy*dy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x / iResolution.y;

    float m;
    m = 1.0 - dfbm(30.0*p, vec2(0.001));
    m *= 0.9;

    vec3 color;
    //color = m * texture(iChannel0, p).xyz + (1.0 - m) * texture(iChannel1, 0.5*p).xyz;
    color = vec3(m);
	fragColor = vec4(color, 1.0);
}
