void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv =  2.0 * vec2(fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

	vec4 bg = texture(iChannel0, fragCoord.xy / iResolution.xy);

    float vignette = 1.0 - (iMouse.y / iResolution.y*length(uv));

    fragColor = bg * vignette;
}
