#define withColor 0
#define maxIter 9
#define timeScale 1.
const vec3 color1 = vec3(.6,.54,.78);
const vec3 color2 = vec3(.44, .64, .3);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;

    int iter = int(mod(iTime*timeScale, float(maxIter)) + 1.);
    #ifdef withColor
    float sum = 0.;
    #endif
	float is = -1.;
    for (int i = 0; i < maxIter+1; i++) {
		if (i > iter) break;

        is *= sign(cos((atan(uv.y,uv.x))*8. + 
                      floor(length(uv)*float(i)*16.) ));
    	#ifdef withColor
        sum += is;
        #endif
   	}

    #ifdef withColor

    float am = (is*.5+.5)*.75 + .25;
    fragColor.xyz = mix( color1 ,
                         color2 ,
                        sum/(float(iter)*2.+1.) ) * am;
    #else
    fragColor.xyz = vec3(is*.5+.5);
    #endif

    fragColor.w = 1.;
}
