void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	fragColor = texture(iChannel0, uv);

    /*
	 *  a b c
	 *  d . e
	 *  f g h
	 */

     // top
    vec4 a = texture(iChannel0, vec2(uv.x-1.0/iResolution.x, uv.y+1.0/iResolution.y));
    vec4 b = texture(iChannel0, vec2(uv.x,      uv.y+1.0/iResolution.y));
    vec4 c = texture(iChannel0, vec2(uv.x+1.0/iResolution.x, uv.y+1.0/iResolution.y));

     // midle
    vec4 d = texture(iChannel0, vec2(uv.x-1.0/iResolution.x, uv.y));
    vec4 e = texture(iChannel0, vec2(uv.x+1.0/iResolution.x, uv.y));

    // bottom
    vec4 f = texture(iChannel0, vec2(uv.x-1.0/iResolution.x, uv.y-1.0/iResolution.y));
    vec4 g = texture(iChannel0, vec2(uv.x,      uv.y-1.0/iResolution.y));
    vec4 h = texture(iChannel0, vec2(uv.x+1.0/iResolution.x, uv.y-1.0/iResolution.y));

    /* Sobel X
     *
	 *  -1 0 +1
	 *  -2 0 +2
	 *  -1 0 +1
	 */ 
    vec4 sobelX = a*(-1.0) + c*(1.0) + d*(-2.0) + e*(2.0) + f*(-1.0) + h*(1.0);

    /* Sobel y
     *
	 *  -1 -2 -1
	 *   0  0  0
	 *  +1 +2 +1
	 */ 
    vec4 sobelY = a*(-1.0) + b*(-2.0) + c*(-1.0) + f*(1.0) + g*(2.0) + h*(1.0);

    fragColor = sobelX + sobelY;

    //make gray
    float gray = dot(fragColor.rgb, vec3(0.299, 0.587, 0.114));
    fragColor = vec4(gray, gray, gray, fragColor.a);
}
