#define FPS 60.0
#define SCALE 1.0
#define ENABLED 1

#if ENABLED
const float kBlurDuration = 1./FPS;
#else
const float kBlurDuration = 0.;
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 fc = fragCoord.xy - mod(fragCoord.xy, SCALE);
	vec2 uv = fc.xy / iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    float time=iTime;
    time=floor(time*FPS)/FPS;
    float dither=texture(iChannel0,fract(fc.xy/iChannelResolution[0].xy/SCALE)).r;
    time+=dither*kBlurDuration;
    uv.x+=sin(time*13.)*.75;
    uv.y+=cos(time*17.)*.25;
    fragColor = vec4(vec3(1.-length(uv)/.05)*25.,1);
}
