//https://www.shadertoy.com/view/ltSyRc
// ==== text utils
float char(vec2 p, int c) { // --- access to the image of ascii code c
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return 0.;
	return textureGrad( iChannel3, p/16. + fract( vec2(c, 15-c/16) / 16. ), 
                        dFdx(p/16.),dFdy(p/16.) ).x;
}

float pInt(vec2 p, float n) { // --- display uint4
    float v = 0.;
    for (float i = 3.; i>=0.; i--) 
        n /=  9.999999, // 10., // for windows :-(
        v += char(p - .5*vec2(i,0), 48+ int(fract(n)*10.) );
    return v;
}

vec4 getData (int id)
{
  int row = int(iChannelResolution[0].y);
  return texelFetch(iChannel0,ivec2(id%row,id/row),0);
  //return textureLod(iChannel0,(vec2(id%row,id/row)+.5)/iChannelResolution[0].xy,0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = vec4(pInt((fragCoord/iResolution.y)*5.,getData(int(iTime*3.)%10).x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  int id = int(fragCoord.x) + int(iResolution.y) * int(fragCoord.y);
  if (id > 7) discard;  
  fragColor = vec4(id);  
}
