vec4 circle2x(vec2 uv, vec2 pos, float rad, float a) {
    vec4 c = vec4(0);

    // tile warp
    c += circle0(uv, vec2(pos.x - iResolution.x, pos.y), rad, rgb(a, a, a));
    c += circle0(uv, vec2(pos.x , pos.y), rad, rgb(a, a, a));
    c += circle0(uv, vec2(pos.x + iResolution.x, pos.y), rad, rgb(a, a, a));


    return c;
}

vec4 circle(vec2 uv, vec2 pos, float rad, float a) {
    vec4 c = vec4(0);

    // tile warp
    c += circle2x(uv, vec2(pos.x, pos.y  - iResolution.y), rad, a);
    c += circle2x(uv, vec2(pos.x, pos.y  ), rad, a);
    c += circle2x(uv, vec2(pos.x, pos.y  + iResolution.y), rad, a);


    return c;
}

