vec2 houseloc = vec2(0.3,0.2);
float housewidth = 0.4;
float househeight = 0.3;
vec3 housecolor = vec3(1.0,0.0,0.0);

vec2 chimneyloc = vec2(0.6,0.7);
float chimneywidth = .05;
float chimneyheight = .1;
vec3 chimneycolor = vec3(1.0,0.8,0.8);

vec2 window1loc = vec2(0.35,0.2);
vec2 window2loc = vec2(0.55,0.2);
float windowwidth = 0.1;
float windowheight = 0.1;
vec3 windowcolor = vec3(1.0,0.0,1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float aspectRatio = iResolution.y/iResolution.x;

    uv.y *= aspectRatio;
    houseloc.y *= aspectRatio;
    chimneyloc.y *= aspectRatio;

    vec4 bglayer = vec4(1.0,1.0,1.0,0.0);
    vec4 layer1 = vec4(0.0,0.0,0.0,0.0);
    vec4 layer2 = vec4(0.0,0.0,0.0,0.0);
    vec4 layer3 = vec4(0.0,0.0,0.0,0.0);

    layer1 = vec4(housecolor, 0.0);
    layer2 = vec4(chimneycolor, 0.0);
    layer3 = vec4(windowcolor, 0.0);

    float interpx = step(houseloc.x, uv.x) - step(houseloc.x+housewidth, uv.x);
    float interpy = step(houseloc.y, uv.y) - step(houseloc.y+househeight, uv.y);
    float interp = interpx*interpy;
    vec4 col = mix(bglayer,layer1, interp);
    interpx = step(chimneyloc.x, uv.x) - step(chimneyloc.x+chimneywidth, uv.x);
    interpy = step(chimneyloc.y, uv.y) - step(chimneyloc.y+chimneyheight, uv.y);
    interp = interpx*interpy;
    col = mix(col,layer2, interp);
    interpx = step(window1loc.x, uv.x) - step(window1loc.x+windowwidth, uv.x)+step(window2loc.x, uv.x) - step(window2loc.x+windowwidth, uv.x);
    interpy = step(window1loc.y, uv.y) - step(window1loc.y+windowheight, uv.y);
    interp = interpx*interpy;
    col = mix(col,layer3, interp);


    // Output to screen
    fragColor = col;
}
