float fCurve(float x)
{
    return 1.0 - (3.0-2.0*abs(x))*x*x;
}

float gradient(vec2 pos, vec2 g)
{
    return dot(g, pos);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - iMouse.xy)/iResolution.y;
    uv *= 10.0; // zoom
    vec2 g = vec2(cos(iTime), sin(iTime));
    float val = fCurve(uv.x) * fCurve(uv.y) * gradient(uv, g);
    fragColor = vec4(val + 0.5);
}
