#define N 100.
#define PI 3.14159265358979

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;

    vec2 p = N * (fragCoord * 2.0 - R) / min(R.x, R.y);	// Normalize

    R.x = p.x;												 // x coordinate of function
    R.y = sin(iTime) * R.x * sin(iTime + R.x);		// y coordinate of function based on x

    vec3 destColor = vec3(1. / length(R - p));

    fragColor = vec4( destColor, 1);
}
