float map(vec3 pos, float radius) {
    vec3 q = fract(pos) * 2.0 - 1.0;
	return length(q) - radius;   
}

const int ITER = 32;
const float GAIN = 0.6;
float trace(vec3 origin, vec3 ray, float freq) {
    float t = 0.0;
    for (int i = 0; i < ITER; ++i) {
     	vec3 p = origin + (ray - vec3(
            sin(iTime) * 0.1,
            cos(iTime) * 0.1,
            0.0
        )) * t;
        float d = map(p, freq * GAIN);
        t += d * 0.5;
    }

    return t;
}

float freqs[4];

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    freqs[0] = texture( iChannel0, vec2( 0.01, 0.25 ) ).x;
	freqs[1] = texture( iChannel0, vec2( 0.07, 0.25 ) ).x;
	freqs[2] = texture( iChannel0, vec2( 0.15, 0.25 ) ).x;
	freqs[3] = texture( iChannel0, vec2( 0.30, 0.25 ) ).x;

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ray = normalize(vec3(uv, 1.0));
    vec3 origin = vec3(0.0, 0.0, iTime / 5.0);
    float t = trace(origin, ray, freqs[3]);
    float fog = 1.0 / (1.0 + pow(t, 2.0) * max(
        									0.05,
                                            abs(sin(iTime) * 0.2 + 0.1)));
    vec3 fc = vec3(fog, fog, fog * 1.5);
    fragColor = vec4(fc, 1.0);
}
