float computeFountain(vec2 aspectNdc, float yRate, float xRate, float gravity) {
    return ((aspectNdc.y * yRate) / (aspectNdc.x * xRate)) - (aspectNdc.x * gravity);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 aspectNdc = (fragCoord.xy - (iResolution.xy / 2.0)) / min(iResolution.x, iResolution.y);
    aspectNdc.x += sign(aspectNdc.x) * 0.1;
    aspectNdc.y += 0.2;
    float loc = computeFountain(aspectNdc, -100.0, 10.0, 100.0);
    float loc2 = computeFountain(aspectNdc, -10.0, 100.0, -100.0);
    float loc3 = loc2 - iTime * sign(aspectNdc.x) * 20.0;
    float intensity = sin(loc) * sin(loc3 + loc * 0.25);
	fragColor = vec4(mix(vec3(0.2, 0.5, 1.0), vec3(0.0, 0.2, 0.6), intensity) * abs(10.0 / (1.0 + loc)), 1.0);
}
