const float PI = 3.141592653589793238462643383;
const float PI2 = PI * 2.0;
const float contrast = 2.0;
const float rasterPower = 20.0;
const float lineInterval = 200.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 temp = vec4(vec3(0.0),1.0);
    vec2 pattern = uv * lineInterval;

    vec4 color = texture(iChannel0, uv);

    temp.r = 0.5 + sin(pattern.x + pattern.y) * 0.5;
    temp.r = pow(temp.r, 1.0 + pow(1.0 - color.r, contrast) * rasterPower);

    fragColor = temp;
}
