void mainImage(out vec4 color, in vec2 pixCoords) {
    vec2 coords = pixCoords / iResolution.y;

    vec2 center = vec2(0.5, 0.5);
    center.x += cos(iTime) * 0.3;
    center.y += sin(iTime) * 0.3;

    float distance = length(coords - center);
    float rayon = 0.2 + 0.1 * cos(iTime * 2.0);

    if (distance < rayon) {
        color = vec4(1.0, 0.0, 0.0, 1.0);
    }

    else {
        color = vec4(1.0, 1.0, 1.0, 1.0);
    }
}
