const float eyeDistance = 1.0;

vec4 color;
vec2 coord;

float eyeHeight = 100.0;
float waterLevel  = iResolution.y / 2.0;
float waveLevel = 40.0;

float coefHeightDif = 5.0;
float coefColorDegrade = 1.0;
float waveLength = 0.01;
float waveHeight = 40.0;


float transSpeed = 20.0;

void waves(float dist)
{
    float y = eyeDistance + (waterLevel/2.0 - eyeHeight) / dist + dist;
    float waveLevelRatio = waveLevel / pow(dist, coefHeightDif);

    float wave = sin(waveLength*coord.x*pow(dist,4.0)+iTime*transSpeed/dist);

    float height = wave+waveLevelRatio+y+waterLevel;

    if(coord.y<height){
        color = vec4(0,0,max(1.1/pow(dist,coefColorDegrade),0.4),1.0);
        color = vec4(0,0.1*dist,dist,1.0);
    }
}

void mainImage( out vec4 color, in vec2 pixCoords )
{
    float centerX = iResolution.x / 2.0;
    float centerY = iResolution.y / 2.0;
    float distance = sqrt(pow(pixCoords.x - centerX, 2.0) + pow(pixCoords.y - centerY, 2.0));
    float radius = iResolution.x / 12.0;
    float origin = 75.0;
    float angle = (pixCoords.x / 35.0);

    color = vec4(0.6,0.6,1.0,1.0);

    float coefScreen = iResolution.y/5.0;
    eyeHeight = cos(iTime*2.0)*coefScreen+2.5*coefScreen;

    color = vec4 (0.8, 0.6, 0.6, 1.0);

    if (distance < radius ){
     		color = vec4(1.0, 0.6, 0.0, 1.0);
    }

    for(float d = 4.0 ; d > 1.0 ; d-=0.4){
        waves(d);
    }
}
