void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    vec2 pos =-1.+2.*(fragCoord.xy/iResolution.xy);//
    	  	 pos*=12.*vec2(1.,.5);;

    float newp=
   	 	       pow(pos.x+pos.y+mod(iTime,.1)*3.,2.)+
    		    pow(pos.x-pos.y-sin(iTime)*3.,2.);

    vec2 n = vec2(.010,.010);
    float vgnte=
   		   		pow(pos.x+pos.y*2.,4.)+
    			 pow(pos.x-pos.y*2.,4.);

    	vgnte*=.1/-cos(.1)*vgnte/vgnte*n.y+n.x;

    	newp<1.?pos.x*=1.*pos.y:newp*=0.;

    for(float f =0.;f<6.;f++)
			pos.y+=newp+sin(f*sin(f)*cos(1.))
        	  	   +atan(pos.x,-pos.y)*pow(pos.y/-pos.x,1.)/tan(iTime);

    float col =.001;   
	float newcol =mix(
        		   col /= .2/(length(pos.xy-newp)-pos.y),
        		   	col += .2/(length(pos.y+newp)),.5);

	fragColor = vec4(vec3(.02,.01,.06),1.)*5.
				 +vec4(vec3(vgnte,vgnte,vgnte-.15)/700.5,1.) 
        	 	  +vec4(1.-vec3(newcol),1.); 
}

float premix (float count ){

float b =(64.), a =(440./8.);      
if(sin(count)>.5) return b;
if(count<sin(count)) return a; 

}

vec2 synthi1(in float ti){

   float note=208.;
   if(sin(ti)>0.8) note +=148.*0.997494;//sin(3./2)
   if(sin(ti)>0.5) note +=70.*0.997494;
   if(sin(ti)>.02) note +=65.*0.997494;
   if(sin(ti)>.2) note +=329.*0.997494;

   return sin(vec2( sin(6.2831* note*ti)))
       		*exp(-.001*ti)*fract(-2.12*ti); 
} 

vec2 synthi(in float ti) {   
return vec2( sin(6.2831*premix(float(synthi1(ti)))*ti));
} 

