// Thanks  https://www.shadertoy.com/view/ltXXDN
#define r(o) fract(sin(vec4(6,9,1,o)*i) * 9e2)
void mainImage(out vec4 f, vec2 v )  {
    float t = iTime;
    vec2 uv=v/iResolution.y;
  	for (float i=0.; i < 10.; i+=.1) 
        f +=  r(0)/8e2
             / pow(
                  length( uv - 3.*fract(sin(t)*0.05+r(5.).zw))
               , 0.6*abs(sin(t*2.+i))+0.8 );  
}
