// just composite everything
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 res = iResolution.xy;
    vec2 uv = fragCoord / res;
    vec4 t = texture(iChannel0, uv);
    float a = texture(iChannel1, uv).r;
    vec4 orig = texture(iChannel2, uv);

	vec4 m = floor(mod(t*12.0, 1.0)+0.5);
    float mr = m.r;
    m.rgb = mr * hsv2rgb(vec3(t.r + iTime*0.5 , 0.45,1.0));
    m.a = mr;


    fragColor = mix(orig, m, mr);
    fragColor = mix(orig, fragColor, step(0.001, t));//step(0.01, t));
    fragColor = mix(fragColor, orig, a);
  	//fragColor = sin(t*6.28*16.0)*0.5 + 0.5;//vec4(m.rgba);
    //fragColor = floor(mod(t*12.0, 1.0)+0.5);
    //fragColor = t;
}

vec2 offset [9];
float spread = 0.75;
float decay = 0.04;
// here we doo the dilation feedback
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 res = iResolution.xy;
    vec2 step = spread / res;
    vec2 uv = fragCoord / res;        
    vec4 sum = texture(iChannel0, uv);

    // 49 taps gives us better results but you could probably find settings that work with less
    for(int y = -7; y <= 7; y++){
        for(int x = -7; x <= 7; x++){
         	vec4 color = texture(iChannel1, uv + vec2(x, y) * step);
            sum = max(color, sum);
        }
    }


    fragColor = sum - decay; 
}

// chromakey from here https://www.shadertoy.com/view/MsS3DW
// this buffer just makes alpha mask

float th = 8.0; // threshold
float a2 = 1.2;
float spill = 1.0;


float getAlpha(vec4 c){
	// First Vlahos assumption: Gf <= a2Bf	
	return 1.0 - th*(c.g-a2*(max(c.r,c.b)));
	}

vec4 despill(vec4 c){
	/// Second Vlahos assumption: max (Gf - Bf,0) <= max(Bf - Rf, 0)
	float sub = max(c.g - mix(c.b, c.r, 0.45), 0.0);
	c.g -= sub;

	/// 
	c.a -= smoothstep(0.25, 0.5, sub*c.a);

	//restore luminance (kind of, I slightly reduced the green weight)
	float luma = dot(c.rgb, vec3(0.350, 0.587,0.164));
	c.r += sub*c.r*2.0*.350/luma;
	c.g += sub*c.g*2.0*.587/luma;
	c.b += sub*c.b*2.0*.164/luma;

	return c;
	}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 fg = texture(iChannel0,uv);

    vec4 ofg = fg;

	fg.a = clamp(getAlpha(fg), 0.0, 1.0);
	fg = despill(fg);    
	fragColor = fg.aaaa; 
}
