vec3 rect(vec2 st) {
    float f = length(max(abs(st) - .3, 0.));
    f = smoothstep(.05, .04, f);
    return vec3(f);
} 

vec3 circle(vec2 st) {
    float f = length(st);

    f = smoothstep(.203, .2, f);
    return vec3(f);
}

vec3 circleRect(vec2 st, float pct) {
//    pct = .01;
    float minSize = .0;
    float maxSize = 10.;
    float currentSize = mix(minSize, maxSize, pct);

    st *= .2 * currentSize + 1.;
    float f = length(max(abs(st) - .1, 0.));
    f = smoothstep(currentSize, currentSize + currentSize/10., f);
    return vec3(f);
}

mat2 rotate2d(float _angle) {
    return mat2(cos(_angle), -sin(_angle),
        		sin(_angle), cos(_angle));
}

mat2 scale(vec2 _scale) {
    return mat2(_scale.x, 0.,
               	0., _scale.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float u_time = iTime;

    vec2 uv = fragCoord/iResolution.y;
    uv.x -= .4;

	vec2 st = uv;


	st-=.5;

    st *= sin(u_time)/2. + 1.;
    st *= length(st) * sin(u_time/2.)/2.+1.;

    vec2 tt = st * 12.;
    float time = u_time/10. * ceil(tt.x) / ceil(tt.y);
    vec3 color = vec3(0.);



    float n = abs(cos(time /50.)+6. * min(st.x, st.y));

    if(n < 0.) {// unused
        st*= 24.;
        st = mod(st, 1.);
        vec2 rectTransform = rotate2d(-u_time*.3) * ((scale(vec2(3.)) * rotate2d(-u_time) * (st - .5)) + .4);
        color += rect(rectTransform);
        vec2 circleTransform = rotate2d(-u_time*.3) * ((scale(vec2(2.)) * rotate2d(-u_time) * (st - .5)) - .4);
        color += circle(circleTransform);
    } else { 
        st *= 48.;
        st = mod(st, 1.);
        color += circleRect(st-.5, min(sin(time), cos(time))/2. + .5);
    }    
    fragColor = vec4(color,1.0);
}
