void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    col *= 0.5;

    vec2 center = vec2(0.0, 0.5);
    float distanceFromCenter = length(uv - center);
    float ringRadius = 1.0 + sin(iTime);
    float ringWidth = 0.5;

    float distance = (distanceFromCenter - ringRadius) / ringWidth;
    distance = clamp( distance, -1.0, 1.0 );

    float wave = 0.5 + 0.5 * cos( distance * 3.14159 );

    float intensity = 0.5;

    col += intensity * wave;


    // Output to screen
    fragColor = vec4(col,1.0);
}
