vec4 square(vec2 center, float size, vec2 uv, vec3 color)
{   
    float valueA = clamp((abs(uv.x-center.x)-size)*400.0, 0.0, 1.0);
    float valueB = clamp((abs(uv.y-center.y)-size)*400.0, 0.0, 1.0);
    float value = clamp(valueB + valueA, 0.0, 1.0);
    // color and alpha
    vec3 blanc = vec3 (1.0,1.0,1.0);
    vec3 final = mix(color, blanc, value);
    float alpha = 1.0 - value;

    return vec4 (final, alpha);
}

vec4 circle(vec2 center, float radius, vec2 uv, vec3 color)
{
    float aspectRatio = iResolution.x / iResolution.y;
	uv.x *= aspectRatio;
    center.x *= aspectRatio;
    float value = clamp((distance(uv , center )- radius)*300.0, 0.0, 1.0);
    // color and alpha
    vec3 blanc = vec3 (1.0,1.0,1.0);
    vec3 final = mix(color, blanc, value);
    float alpha = 1.0 - value;

	return vec4 (final, alpha);
}

vec4 monMix (vec4 color1, vec4 color2, float scalar)
{
    vec3 value = vec3(color2*scalar + color1*(1.0-scalar));
    return vec4(value, 1.0);

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;

    // Param for the Square
    vec2 centerA = vec2(0.5, 0.8);
    float size = 0.1;
    vec3 colorA = vec3 (1.0,0.0,0.0);

    // Param for the Circle
    vec2 centerB = vec2(0.5, 0.5);
    float radius = 0.25;
    vec3 colorB = vec3 (0.0,1.0,0.0);


    // COMPO
    vec4 layer1 = square(centerA, size, uv, colorA);
    vec4 layer2 = circle(centerB, radius, uv, colorB);
    vec4 master = monMix(layer1, layer2, layer2.a);

	fragColor = vec4(master);
}
