#define time (iTime*1000.)
#define resolution (iResolution.xy)

#define PI 3.1415
mat2 rotate(float theta) {
	return mat2(
		cos(theta), -sin(theta),
		sin(theta), cos(theta)
	);
}

float grid(vec2 pos) {
	vec2 val = sin(pos);
	return max(val.x, val.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 pos = fragCoord / resolution;
	pos-=.5;
	vec2 spiralPos = rotate(sin(time/1000.)*length(pos))*pos;
	spiralPos*=100.;
	float mul = min(1.+sin(time/500.+length(pos)),1.);
	fragColor = mix(vec4(.5,0,0,0),mul*vec4(0,.5,0,0),grid(spiralPos));
}
