void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


	vec3 final_color = vec3(1.0);
	vec3 bg_color = vec3(-0.20);
	vec3 wave_color = vec3(0.10);
    float speed = 100.0;


	// To create the waves
	float wave_width = 1.1;
	uv  = -1.0 + 2.5 * uv;
	uv.y += 0.011;
	for(float i = 0.0; i < 10.0; i++) {
//		uv.y += ( 0.107 * sin(uv.x + i/17.0 + iTime ));   
		uv.y += ( 0.257 * sin(uv.x + i/8.0 - iTime * speed /70.0 ));  
//        uv.x = 1.1 * iTime;
		wave_width = abs(1.1 / (130.0 * uv.y /iMouse.x*300.0 +20.0))+0.0001*iTime;
		wave_color += vec3(wave_width * 0.9/ iMouse.y*300.0, wave_width, wave_width * 2.9);
	}

	final_color = bg_color + wave_color;


	fragColor = vec4(final_color, 0.200);

    // Based on a Great shader from bonniem modified by me
}
