// Many sources from eddietree
// https://www.shadertoy.com/view/XljXWD

#define SHADE 0

const float MAX_TRACE_DISTANCE = 10.0;
const float INTERSECTION_PRECISION = 0.001;
const int NUM_OF_TRACE_STEPS = 32;
const float eps = 0.;//.0001;

float sdSphere(vec3 p, float radius) {
    return length(p) - radius;
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void doCamera( out vec3 camPos, out vec3 camTar, in float time, in vec2 mouse ){
    float radius 	= 8. + sin(time*.25)*2.;
    float theta 	= 0.3 + 5.0*mouse.x - iTime*0.15;
    float phi 		= .3 + 6.28 * mouse.y ;//.5 + sin(iTime*.05) *2.14159 ;//mouse.y - iTime*0.5;

    float pos_x = radius * cos(theta) * sin(phi);
    float pos_z = radius * sin(theta) * sin(phi);
    float pos_y = radius * cos(phi);

    camPos = vec3(pos_x, pos_y, pos_z);
    camTar = vec3(0.0,0.0,0.0);
}

float smin( float a, float b, float k ){
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

float opRep( vec3 p, vec3 c )
{
    vec3 q = mod(p,c)-0.5*c;
    return sdSphere( q, .25 );
}

float opScale( vec3 p, float s )
{
    return sdSphere(p/s, s)*s;
}

float opS( float d1, float d2 ){
    return max(-d1,d2);
}

float opU( float d1, float d2 ){
    return min(d1,d2);
}

// checks to see which intersection is closer
// and makes the y of the vec2 be the proper id
vec2 opU( vec2 d1, vec2 d2 ){
	return (d1.x<d2.x) ? d1 : d2; 
}

float opI( float d1, float d2 ){
    return max(d1,d2);
}

// noise func
float hash( float n ) { return fract(sin(n)*753.5453123); }
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*157.0 + 113.0*p.z;
    return mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                   mix( hash(n+157.0), hash(n+158.0),f.x),f.y),
               mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                   mix( hash(n+270.0), hash(n+271.0),f.x),f.y),f.z);
}

vec2 doModel( vec3 pos ){  


    //float sphere = sdSphere(pos, 1.75  ) + noise(pos * 1.18 + iTime*0.5);   
    float sphere = opRep(pos+vec3(1.8+iTime*.2, 0., 0.), vec3(.25)) + opScale(pos*.225, noise(pos+pos * .1 + iTime*0.25));//noise(pos * 1.5 + iTime*0.25);   
    float t1 = sphere;

    // Just keep my old code to compare with aiekick implementation
    /*vec3 c = vec3(1.2, eps, eps);
    vec3 q = mod(pos ,c)-0.5*c;
    t1 = smin( t1, sdTorus( q, vec2(.7, .2 )), 2.0 );
    c = vec3(eps, eps, 1.2);
    q = mod(pos ,c)-0.5*c;
    t1 = smin( t1, sdTorus( q, vec2(0.7, .2 ) ), 2.0 );
    c = vec3(eps, .1, eps  );
    q = mod(pos ,c)-0.5*c;
    t1 = smin( t1, sdTorus( q, vec2(0.7, .2 ) )+ opScale(pos*.05, noise(pos+pos * .1 + iTime*0.25)), 3.0 );
    //t1 = smin( t1, sdTorus( pos + vec3(0.,1.,-2.0), vec2(0.7, .2 ) ), 3.0 );
	*/
    vec3 q;
	vec3 c = vec3(1.2, .1, 1.2);
	q = vec3(mod(pos.x ,c.x)-0.5*c.x,pos.yz);
	t1 = smin( t1, sdTorus( q, vec2(.7, .2 )), 2.0 );
	q = vec3(pos.xy,mod(pos.z ,c.z)-0.5*c.z);
	t1 = smin( t1, sdTorus( q, vec2(0.7, .2 ) ), 2.0 );
	q = vec3(pos.x,mod(pos.y ,c.y)-0.5*c.y,pos.z);
	t1 = smin( t1, sdTorus( q, vec2(0.7, .2 ) )+ opScale(pos*.05, noise(pos+pos * .1 + iTime*0.25)), 3.0 );

   	return vec2( t1, 1.0 );
}

float shadow( in vec3 ro, in vec3 rd )
{
    const float k = 2.0;

    const int maxSteps = 10;
    float t = 0.0;
    float res = 1.0;

    for(int i = 0; i < maxSteps; ++i) {

        float d = doModel(ro + rd*t).x;

        if(d < INTERSECTION_PRECISION) {

            return 0.0;
        }

        res = min( res, k*d/t );
        t += d;
    }

    return res;
}


float ambientOcclusion( in vec3 ro, in vec3 rd ){

    const int maxSteps = 7;
    const float stepSize = 0.05;

    float t = 0.0;
    float res = 0.0;

    // starting d
    float d0 = doModel(ro).x;

    for(int i = 0; i < maxSteps; ++i) {

        float d = doModel(ro + rd*t).x;
		float diff = max(d-d0, 0.0);

        res += diff;

        t += stepSize;
    }

    return res;
}

vec3 calcNormal( in vec3 pos ){

	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    doModel(pos+eps.xyy).x - doModel(pos-eps.xyy).x,
	    doModel(pos+eps.yxy).x - doModel(pos-eps.yxy).x,
	    doModel(pos+eps.yyx).x - doModel(pos-eps.yyx).x );
	return normalize(nor);
}

void doShading( vec3 ro , vec3 rd, inout vec3 color, vec3 currPos ){

    if(SHADE==0){
    	vec3 lightDir 	= normalize(vec3(1.0,0.4,0.0));
    	vec3 normal 	= calcNormal( currPos );
    	vec3 normal_distorted = calcNormal( currPos +  noise(currPos*1.5 + vec3(0.0,0.0,sin(iTime*0.75))) );
		float shadowVal = shadow( currPos - rd* 0.1, lightDir  );
    	float ao = ambientOcclusion( currPos - normal*0.01, normal );

    	float ndotl = abs(dot( -rd, normal ));
    	float ndotl_distorted = abs(dot( -rd, normal_distorted ));
    	float rim = pow(1.0-ndotl, 6.0);
    	float rim_distorted = pow(1.0-ndotl_distorted, 6.0);

	    color = texture (iChannel0, reflect( rd, normal ) ).xyz;
	    color = mix( color, vec3(1., .7, .4), rim_distorted+0.5 );
    	color += texture (iChannel0, reflect( rd, normal_distorted ) ).xyz * rim + vec3(rim*.8);

    	color = mix( color, vec3(1., .7, .4), rim_distorted+0.1 );
	    color += rim;

    	color *= vec3(mix(0.9,1.0,shadowVal));

	    color *= vec3(mix(0.4,1.0,ao));
    }else{
    //------------------
    	vec3 lightDir 	= normalize(vec3(0.0,-.4,0.0));
     	vec3 normal = calcNormal( currPos - rd * noise(currPos*70.0)*0.1 );

    	//float shadowVal = shadow( currPos - rayDirection* 0.03, lightDir  );
	    float ao = ambientOcclusion( currPos - normal*0.01, normal );
	   	 float ndotl = abs(dot( -rd, normal ));
   	 	float rim = pow(1.0-ndotl, 3.5);
    	float ndotl_distorted = abs(dot( -rd, normal ));
	    float rim_distorted = pow(1.0-ndotl_distorted, 6.0);
    	float specular = pow( dot( lightDir, normal ), 3.0);

	    vec3 reflectionColor = texture (iChannel0, reflect( rd, normal )).xyz;

	    color = reflectionColor;
	    color = mix( color, vec3(1., .7, .4), rim_distorted+0.2 );
    	color *= vec3(mix(0.6,1.0,ao));
	    color += specular *.5;    
    	//color += rim * .5;

    }

}

vec3 rayPlaneIntersection( vec3 ro, vec3 rd, vec4 plane ){
	float t = -( dot(ro, plane.xyz) + plane.w) / dot( rd, plane.xyz );
	return ro + t * rd;
}

bool renderRayMarch(vec3 ro, vec3 rd, inout vec3 color ) {
    const int maxSteps = NUM_OF_TRACE_STEPS;

    float t = 0.0;
    float d = 0.0;

    for(int i = 0; i < maxSteps; ++i) {
        vec3 currPos = ro + rd * t;
        d = doModel(currPos).x;
        if(d < INTERSECTION_PRECISION) {
            break;
        }        
        t += d;
    }

    if(d < INTERSECTION_PRECISION) {
        vec3 currPos = ro + rd * t;
        doShading( ro, rd, color, currPos );

        return true;
    }

    vec3 planePoint = rayPlaneIntersection(ro, rd, vec4(0.0, -1.0, 0.0, 1.0));
	float shadowFloor = shadow( planePoint, vec3(0.0,.5,0.0));
	color = color * mix( .92, 1.0, shadowFloor );// - length(ro)
	color -= vec3(.0, .6, .4);

    return false;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,1.0) ); // 2.0 is the lens length

    // calc color
    vec3 col = vec3(0.3, .7, .4);
    renderRayMarch( ro, rd, col );

    // vignette, OF COURSE
    float vignette = 1.0-smoothstep(1.0,2.5, length(p));
    col.xyz *= mix( 0.7, 1.0, vignette);

    fragColor = vec4( col , 1. );
}
