void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy / iResolution.xy;
   	xy.y = 1.0 - xy.y;
    vec4 texColor = texture(iChannel0, xy);

    float gray = (texColor.r + texColor.g + texColor.b) / 3.0;

    float rOffset = (texColor.r - gray) * abs(sin(iTime));
    float gOffset = (texColor.g - gray) * abs(sin(iTime));
    float bOffset = (texColor.b - gray) * abs(sin(iTime));

    texColor.r -= rOffset;
    texColor.g -= gOffset;
    texColor.b -= bOffset;                  


    fragColor = texColor;
}
