void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

float random(vec2 st) {
    return fract(sin(dot(st, vec2(12.9898,78.233))) * 43758.5453123);
}

vec4 initialColor(vec2 fragCoord) {
	return vec4(0.0, random(fragCoord / iResolution.xy), 0.0, 1.0);
}

bool status(vec2 fragCoord, float h, float v) {
	return texture(iChannel0, (fragCoord + vec2(h, v)) / iResolution.xy).g < 0.5 ? false : true;
}

vec4 updateColor(vec2 fragCoord) {
    bool isAlive = status(fragCoord, 0.0, 0.0);

    int sum = 0;
    sum += status(fragCoord, -1.0, 1.0) ? 1 : 0;
    sum += status(fragCoord, 0.0, 1.0) ? 1 : 0;
    sum += status(fragCoord, 1.0, 1.0) ? 1 : 0;
    sum += status(fragCoord, -1.0, 0.0) ? 1 : 0;
    sum += status(fragCoord, 1.0, 0.0) ? 1 : 0;
    sum += status(fragCoord, -1.0, -1.0) ? 1 : 0;
    sum += status(fragCoord, 0.0, -1.0) ? 1 : 0;
    sum += status(fragCoord, 1.0, -1.0) ? 1 : 0;

    return ((isAlive && (sum == 2 || sum == 3)) || (!isAlive && sum == 3)) ? vec4(0.0, 1.0, 0.0, 1.0) : vec4(0.0, 0.0, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor =  iFrame == 0 ? initialColor(fragCoord) : updateColor(fragCoord);
}
