float circle(in vec2 _st, in vec2 _center, in float _radius){
    vec2 dist = _st-_center;
	return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(dist,dist)*4.0);
}

vec2 iter(in vec2 _z, in vec2 _c) {
    _z = mat2(_z,-_z.y,_z.x)*_z + _c;
    return _z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 z, c;

    c = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    z = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	vec2 w = z;
    vec2 d = z;
    int i;

    for(int i=0; i<1000; i++) {
        z = iter(z,c);
		if(dot(z,z)>4.) break;
    }

    for(i=0; i<1000; i++) {
        w = iter(w,d);
		if(dot(w,w)>4.) break;
    }

    //fragColor = vec4(i == iter ? 0.0 : float(i) / 100.0);
    vec4 julia = texture(iChannel0,(z-vec2(0.0,1.0))/2.0);
    vec4 mandelbrot = (1.0-.5*vec4(i == 1000 ? 1.0 : float(i) / 100.0));
    float dot = circle(fragCoord,iMouse.xy,50.0);
    fragColor = julia*mandelbrot*(1.0-dot)+vec4(1.0,0.0,0.0,1.0)*dot;
}
