//Ethan Alexander Shulman 2018
void mainImage( out vec4 o, vec2 u )
{
    o -= o;
    for (int i = 0; i < 6; i++) {
        if (i > (iFrame/180)%6) break;
        float fi = float(i)*.04;
        o += abs(tan(length(mod(u*mat2(cos(vec4(u.x,u.y,-u.y,u.x)/100.))/2000.,fi)-fi*.5)*(40./(1.+fi))-mod(iDate.w*(1.+vec4(fi*.01,fi*.03+sin(iDate.w*1e-5),fi*.002,0.))+u.x*.034+cos(u.y*.03),2e3)))*.02;
    }
}
