float linewidth = 0.03;

float createWave(vec2 uv, float frequency, float amplitude, float speed)
{
  	float phase = iTime * speed;
 	//amplitude at position x (y value)
  	float yOffset = sin(uv.x * frequency + phase);
  	yOffset += sin(uv.x*frequency*2.1 + phase)*4.5;
    yOffset += sin(uv.x*frequency*1.72 + phase*1.121)*4.0;
	yOffset += sin(uv.x*frequency*2.221 + phase*0.437)*5.0;
	yOffset += sin(uv.x*frequency*3.1122+ phase*4.269)*2.5;

  	return yOffset * amplitude;
}

float drawWave(vec2 uv, float yOffset)
{
    // pixel intensity (line)
    float wave = abs((uv.y / linewidth) + yOffset);
    wave = 1.0 - wave;

    return wave;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((fragCoord - (iResolution.xy * 0.5)) / iResolution.xy) * -2.0;

    float wave1a = createWave(uv, 8.0, 1.0, 10.0);
   	float wave1 = drawWave(uv, wave1a);

    float wave2a = createWave(uv, 5.0, 0.5, 2.0);
    float wave2 = drawWave(uv + vec2(0.0, -0.5), wave2a);

    float wave3a = createWave(uv, 20.0, 0.05, 7.0);
    float wave3 = drawWave(uv + vec2(0.0, 0.5), wave3a);

    float wave =  max(wave1, max(wave2,wave3));

    wave = clamp(wave, 0.0, 1.0);


    float r = length(uv);
    vec3 bg = mix( vec3(0.36, 0.81, 0.9), vec3(0.12, 0.27, 0.3), r);
    bg = mix(bg, vec3(0.9, 0.7, 0.9), wave);

	fragColor = vec4(bg, 1.0);
}
