#define LPOS vec3(cos(t)*.8, sin(t)*.8, -t-1.5)
#define I_MAX		100
#define E			0.001

vec4	march(vec3 pos, vec3 dir);
vec3	camera(vec2 uv);
void	rotate(inout vec2 v, float angle);
vec2	rot_t;
vec3	h;
vec3	base;
vec3	light_pos;
vec3	light_green = vec3(.5,.7,.4);
vec3	light_blue = vec3(.1,.5,.7);
vec3	light_red = vec3(.7,.2,.1);
vec3	uglob;
float 	t;

void mainImage(out vec4 c_out, in vec2 f)
{
    uglob*=0.;
    vec3	col = vec3(0., 0., 0.);
    vec2 R = iResolution.xy,
          uv  = vec2(f-R/2.) / R.y;
	vec3	dir = camera(uv);
    vec3	pos = vec3(-.0, .0, 1.0-iTime*.5);
    t = iTime;

    dir=normalize(dir);
    vec4	inter = (march(pos, dir));

    col.xyz = vec3(.99, .9, .7)*((inter.w*.1051)+(1.-inter.x*.1)*.06125);
    col.xyz += -h*.0053061251;
    c_out =  vec4(1.-col,1.0);
}

float	scene(vec3 p)
{
    float	mind = 1e5;

    p.xy += .5;
    p.z += .52*t;
    light_pos.xy = vec2(cos(p.z*4. ),sin(p.z*4. ))*.15;
    p.xy = fract(p.xy)-.5;
    p.z += .52*t;
    rotate(p.xy, iTime*1.+p.z*.75);
    p.xy = abs(p.xy)-.25;
    rotate(p.xy, iTime*4.+p.z*2.);
    light_pos.z = p.z;

    p.xy = abs(p.xy)-.06125-sin(p.z)*.051-light_pos.xy*.251;

    mind = min(mind, 
              length(p.xy)+.0012501
              );

    h+= 1.-vec3(.01, .1, .31)*1./(mind*mind +.1);
    return(mind);
}


vec4	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec4	s = vec4(0.0, 0.0, 0.0, 0.0);

    for (int i = -1; i < I_MAX; ++i)
    {
    	p = pos + dir * dist.y;
        dist.x = scene(p);
        dist.y += dist.x;
        if (dist.x < E )
        {
            s.y = 1.;
            break;
        }
        s.x++;
    }
    s.w = dist.y+dist.x;
    return (s);
}

// Utilities

void rotate(inout vec2 v, float angle)
{
	v = vec2(cos(angle)*v.x+sin(angle)*v.y,-sin(angle)*v.x+cos(angle)*v.y);
}

vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}


vec3 calcNormal( in vec3 pos, float e, vec3 dir)
{
    vec3 eps = vec3(e,0.0,0.0);

	return normalize(vec3(
           march(pos+eps.xyy, dir).w - march(pos-eps.xyy, dir).w,
           march(pos+eps.yxy, dir).w - march(pos-eps.yxy, dir).w,
           march(pos+eps.yyx, dir).w - march(pos-eps.yyx, dir).w ));
}

vec3	camera(vec2 uv)
{
    float		fov = 1.;
	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}
