void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // constants
    float animationSpeed = 1.5;
    float startSecond = 1.0; // show the lightness between sceconds 1 and 2
    float endSecond = 2.0;
    float totalSeconds = 4.0; // replay every 4 seconds

    // calculations
    float animationTime = 0.0;
    float timer = iTime;
    while (timer > totalSeconds) {
        timer -= totalSeconds;
    }

    float innerAnimationTimer = 0.0;
    if (timer > startSecond && timer <= endSecond) {
        innerAnimationTimer = (timer - startSecond) / (endSecond - startSecond);
    }

	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 textureColor = texture(iChannel0, vec2(uv.x, uv.y));

    // if the color is black (or close to black), just use its real black color
    if (innerAnimationTimer == 0.0 || (textureColor.x < 0.2 && textureColor.y < 0.2 && textureColor.z < 0.2)) {
		fragColor = textureColor;
    }
    else { // otherwise, we will either show the real color or lighten the color, depending on some factors
        float timeOffset = 1.0 - (animationSpeed * (innerAnimationTimer - floor(innerAnimationTimer)));
        if (uv.x > timeOffset && uv.x < timeOffset + 0.05) { // show lightened color because uv.x coordinate lies within the lighten zone
        	fragColor = vec4(1.4, 1.4, 1.4, 1.0) * textureColor;
        }
        else {
        	fragColor = textureColor; // show regular color
        }
    }
}
