void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    float width = 0.03;
    float halfWidth = width * 0.5; 
    float height = sqrt(3.0) / 2.0 * width;

    vec2 R = iResolution.xy;
    vec2 destCoord = fragCoord / R.y;

    float y = floor(destCoord.y / height) * height;
    float yIndex = floor(mod(destCoord.y / height, 2.0));
    float xOffset = (yIndex < 1.0) ? halfWidth : 0.0;

    float x = floor((destCoord.x + xOffset) / width) * width; 

    vec2 cellCenter = vec2(x + halfWidth, y + height*0.5); 

    float luma = 1.0 - (distance(destCoord + vec2(xOffset, 0), cellCenter) / halfWidth); 

    vec3 fragment = texture(iChannel0, cellCenter*R.y/R).xyz;

    luma *= dot(pow(fragment, vec3(2.2)), 
                vec3(0.2126, 0.7152, 0.0722));
    luma = smoothstep(0.0, 0.1, smoothstep (0.0, 1.0, luma));

	fragColor = vec4(fragment * luma, 1.0); 
}
