/**/  // -4 by coyote , -3 by Fabrice

void mainImage(out vec4 O, vec2 U) {
    O.xyz = iResolution;  O = length(U+U-O.xz) / O;
    O = ( .6 + .6 * cos( 7.5 * O.y + vec4(0,21,23,0) ) ) * smoothstep(.5,.25, abs(O.y-1.37) ); }

