// By vojd 2016
// Simplified by FabriceNeyret2

float metaball(vec2 p, float r) 
{   
  return r/dot(p,p);  
}

vec4 hs(float h, float s) {   
  return 1.-s + s*clamp( abs( fract( h + vec4(3,2,1,0)/3. ) * 6. - 3. ) - 1., 0., 1. );  
}


void mainImage( out vec4 O,  vec2 U )
{
  vec2 R = iResolution.xy;
       U = ( U+U-R ) / R.y;

  float t = iTime,
        d = metaball(vec2(U.x + sin(t), U.y), .1)
          + metaball(vec2(U.x, U.y + sin(t)), .1);

  O = hs(length(U) * sin(t * .01), .5);
//  if (d > .707) O -= .4083;    

}
