void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy / iResolution.xy;	// Condensing this into one line
    xy.y = xy.y;
    vec4 frontVid = texture(iChannel0, xy);	// Get the pixel at xy from iChannel0
	vec4 backVid = texture(iChannel1, xy);

    if (frontVid.g > 0.58 && frontVid.r < 0.66 && frontVid.g < 0.66) {
    	frontVid = backVid;
    }

    fragColor = frontVid;						// Set the screen pixel to that color
}
