void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy)-0.5;        
	float gt = iTime;
	float algo_g = 0.0;


    uv.y += 0.195;
	algo_g += cos(sin(uv.x) * uv.y + sin(gt) + cos(uv.x));
	algo_g += tan(uv.y + (1.0 - uv.x) + cos(uv.x) + uv.x);
	algo_g *= cos((1.0 - uv.y) * 4.03 * uv.x * uv.x);
	algo_g += fract(cos(uv.x) + gt + sin(gt) + (1.0 - uv.y));
	algo_g += tan((uv.x * sin(gt)) * (uv.x * sin(gt)) * (uv.x * sin(gt)) + (uv.x * sin(gt)));
	algo_g += tan(sin(uv.x) * sin(uv.x) * cos(uv.x) + (uv.x * sin(gt)));
	algo_g += distance(cos(uv.x) * (1.0 - uv.x) * sin(uv.x) * (1.0 - uv.x), (1.0 - uv.x) + (1.0 - uv.x) * uv.y * sin(uv.x));
	algo_g += distance(sin(uv.x) * 1.78 * (1.0 - uv.y) * sin(uv.x), sin(gt) + 1.79 + uv.x * sin(gt));



	algo_g = (1.0 - algo_g); algo_g = clamp(algo_g, 0.0, 1.0);

	fragColor = vec4(0.0, algo_g, 0.0, 1.0);
}
