//whith the helpe of this https://www.shadertoy.com/view/XtSfDV
#define wheelSize .75

#define S(v) smoothstep( 2./R.y, -2./R.y, v )
#define B(v,e) S( abs(v) - e )


// x resolution 500
// y resolution 281



vec4 chakra(vec2 u,vec2 pos,float size ,vec3 color)    {  

    float speed  = 1.0;
    vec2 u1 = u  ;
	vec2  R = iResolution.xy;


    vec2  U =u1 - pos; 

    float l =2.29* length(U)/size;
    float d =.131;      
    float a = mod( atan(U.y,U.x) +d +speed*iTime, d+d ) - d;   
    float c =   
         	- B(l,.1)  
    		- B(l,.22)*1.4 
        	+ B(l,.26) 
            + B(l-1.,.07)        
            + B(a*l,.03)*S(l-1.1)
            + B((a+d)*l,.03)*S(abs(l-.92)-.02) 
            + B(l-1.12,.02)*S(abs(a)-.05) 
            ;
      return vec4(color,c);        

 }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord.xy/iResolution.xy;

   vec4 backGround = vec4(135.,206.,250.,255.0)/255.;
   vec4 backGroundWhite = vec4(1.0);

   vec4 backGroundBlack = vec4(0.,0.,0.,255.0)/255.;
   vec3 blue =  vec3(.0,0.0,1.0);
   vec2 pos = vec2(500.,281.) * abs(sin(iTime));
   vec4 chakra1 = chakra(fragCoord ,iMouse.xy,281.0*wheelSize,blue );



   fragColor = mix(backGround,chakra1,chakra1.a);



}
