#define AA 1
vec3 BrickText (vec3 u){
    u +=(texture(iChannel0,u).x)/120.;
    vec3 col = vec3(.6,.25,.1);
    if (mod(u.y,.1) < .007 || 
        (mod(u.x + 
                    (mod(u.y,.2)-mod(u.y,.1))
                    ,.2) < .007)||
        (mod(u.z + 
                    (mod(u.y,.2)-mod(u.y,.1))
                    ,.2) < .007)

       )col = vec3(.8,.8,.7);
        col *= (texture(iChannel0,u*5.).x)/5.+.9;
        col *= .8;
        return col;
    }
vec3 AaBrickText (vec3 p)
{
    vec3 c = vec3(0.); //9X MSAA, vertices and center of a cube. Helps reduce moire patterns.
    if(AA == 1){
    c += BrickText(p);
    c += BrickText(p + vec3(-.001,-.001,-.001));
    c += BrickText(p + vec3(-.001,-.001,.001));
    c += BrickText(p + vec3(-.001,.001,-.001));
    c += BrickText(p + vec3(-.001,.001,.001));
    c += BrickText(p + vec3(.001,-.001,-.001));
    c += BrickText(p + vec3(.001,-.001,.001));
    c += BrickText(p + vec3(.001,.001,-.001));
    c += BrickText(p + vec3(.001,.001,.001));
    return c/9.;
    }
    else
    {
        return(BrickText(p));
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    fragColor = vec4(AaBrickText(vec3(uv,iTime/20.)),1.0);
}
