void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;
	vec4 rgba = vec4(0.0,0.0,0.0,1.0);

    vec4 texColor = texture(iChannel0,uv);
    vec4 texColor2 = texture(iChannel1,uv);
    if ((texColor2.r >= 0.01 && texColor2.r <= 0.14) && 
       (texColor2.g >= 0.61 && texColor2.g <= 0.67) && 
       (texColor2.b >= 0.13 && texColor2.b <= 0.25)) {
		texColor2.r = texColor.r;
        texColor2.g = texColor.g;
        texColor2.b = texColor.b;
    }


    fragColor = texColor2;
}
