void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float s = 0.008;
    vec2 p = (-1.0 + 2.0 * fragCoord.xy / min(iResolution.x, iResolution.y));
    vec3 col = vec3(1.0, 1.0, 1.0);

    if (abs(p.x) < s || abs(p.y) < s) {
        col = vec3(0.0, 0.0, 0.0);
    }

    if (abs(mod(p.x, 0.1)) < s && p.y < 0.05 && p.y > .0) {
	col = vec3(0.0, 0.5, 0.3);
    }

    if (abs(mod(p.y, 0.1)) < s && p.x < 0.05 && p.x > .0) {
	col = vec3(0.0, 0.5, 0.3);
    }

    if (abs(p.x - p.y) < s) {
	col = vec3(1.0, 0.0, 0.0);
    }

    if (abs(0.5 * sin(p.x * 10.0) - p.y) < s) {
	col = vec3(0.0, 0.0, 1.0);
    }

    if (abs(p.x * p.x + p.y * p.y - 1.0) < s) {
	col = vec3(0.0, 1.0, 1.0);
    }

    fragColor = vec4(col, 1.0);
}
