float point(vec2 fragCoord, vec2 position, float width) {
    vec2 p = vec2(1.0) / iResolution.xy;

    float dist = distance(fragCoord, position * iResolution.xy);

    return smoothstep(width, width + 0.5, dist);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 position = vec2(0.5);
    if (iMouse.z > 0.0) {
        position = iMouse.xy / iResolution.xy;
    }

    float width = 10.0;

    fragColor = vec4(point(fragCoord, position, width));
}
