// see also https://www.shadertoy.com/view/XlGXDy

void mainImage( out vec4 O, vec2 U )
{
	O = texture(iChannel0, U / iResolution.xy);
}

bool keyToggle(int ascii) {
	return (texture(iChannel2,vec2((.5+float(ascii))/256.,0.75)).x > 0.);
}

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;       
    if (keyToggle(32)) { O = texture(iChannel0, U); return; } // to stop in fullscreen


    U *= 8.;                         // 8x8 tiles
    U += U.x<7. ?  vec2(1,0)         // read right tile
                : -vec2(7,1);        // read left of bottom line
    O = texture(iChannel0, U/8.);
    O.b = O.g * ( mod(U.x,2.)<1. ? 1. : .8 ); // show tiles

    if (U.x<1. && U.y <0.)           // new data in bottom right tile
        U = fract(U),
	    O = vec4( texture(iChannel1, vec2(U.x, .75)).x < U.y ), // or 1.-U.x ?
        O.r += step(U.y,.05- mod(floor(iDate.w),2.)); 
     // O.gb -= step(length(U-.9+mod(floor(iDate.w),2.)), .05);
}
