//Stephen Costello
//Assignment1 - Checkerboard

float rows = 4.0; //Number of rows in the checkerboard
float cols = 4.0; //Number of columns in the checkerboard

//Start of function
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(0.0,0.0,0.0,1.0);

    float cRow = floor(uv.y * rows); //Current row of uv
    float cCol = floor(uv.x * cols); //Current column of uv

    //if the row and col are even, make it black
    //if the row is even but the col is odd, make it white
    //if the row is odd and the col is even, make it white
    //if the row is odd and the col is odd, make it black
    if(mod(cRow, 2.0) == 0.0)
    {

        if(mod(cCol, 2.0) == 0.0)
        {
            fragColor = vec4(0.0, 0.0, 0.0, 1.0); //black
        }
        else
        {
            fragColor = vec4(1.0, 1.0, 1.0, 1.0); //white
        }

    }
    else if(mod(cCol, 2.0) == 0.0)
    {
    	fragColor = vec4(1.0, 1.0, 1.0, 1.0); //white
    }

    else
    {
    	fragColor = vec4(0.0, 0.0, 0.0, 1.0); //black
    }

}
