float plot(vec2 st, float pct){
  return  smoothstep( pct-0.2, pct, st.y) - 
          smoothstep( pct, pct+0.02, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord.xy / iResolution.xy;

    //float y = smoothstep(0.1,0.9,sin(iTime));
    //float y = smoothstep(0.1,0.9,sin(st.x*iTime));
    float y = smoothstep(0.1,0.9,sin(st.y*iTime*200.0));

    vec3 color = vec3(y);
    float pct = plot(st,y);
    color = (1.0-pct)*color + pct*vec3(1.000,0.307,0.963)*.3;

    fragColor = vec4(color, 1.0);
}
