float columns = 10.0;
float rows = 10.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 newColor;
    float temp = floor(mod((uv.x*columns), 2.0)); //turns on/off based on x value
    float temp2 = floor(mod((uv.y*rows), 2.0));  //turns on/off based on y value
    float yesno = abs(0.0-temp+temp2); //determines whether to turn color on/off based on temp&temp2
    newColor = vec4(yesno, yesno, yesno,1.0);  //sets color to white or black

    // Output to screen
    fragColor = newColor;
}
