vec2 p;
float size = 0.1;
float blur = 0.01;

float Circle(vec2 uv, vec2 p, float r, float blur)
{
	float d = length(uv-p); //Postion
	float c = smoothstep(r,r-blur,d); //Draw a circle and blur
	return c;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy/iResolution.xy; // uv = 0~1
	uv -= .5; //circle to center
    if(iMouse.z>0.0)
        p = vec2(iMouse.xy/iResolution.xy-0.5); //Mouse xy
    else 
        p = vec2(0.0); //Center

    p.x *=iResolution.x/iResolution.y; //Mouse Offset
	uv.x *= iResolution.x/iResolution.y; //Circle.x Offset
	float c = Circle(uv,p,size,blur);
	fragColor = vec4(vec3(c),1.0); //Out
}
