void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = texture(iChannel1,uv);

    if (mod(uv.y + iTime, .003) <= .0005) col *= .8;

    fragColor = col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 offset = vec2(
        fract(sin(iTime*uv.x*10030.643)*uv.y*1234000.63),
        fract(cos(iTime*uv.x*25413.4246)*uv.y*1234530.2345)
        );
    offset *= .01;

    vec4 col = (texture(iChannel0,uv)+texture(iChannel1,uv + offset))/2.;

    fragColor = col;
}
