const float PI = 3.14159265358979;

const float c1 = 0.8;
const float c2 = 0.01;
const float c3 = 0.49;

mat2 rot(float t){
    return mat2(cos(t), -sin(t), sin(t), cos(t));
}

float line(vec2 p, float w){
    // return (1.0 - step(w, abs(p.y)));
    return (1.0 - smoothstep(0.0, w, abs(p.y)));
    // return smoothstep(w, -w, abs(p.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    p.x += 0.3;

    float f = textureLod( iChannel0, p/256.0, 0.0 ).x;

    vec3 draw = vec3(0.0);

    float period = 5.0;
    float t = mod(iTime * 0.2, period);

    for (int i=0; i<3; i++) {
	    float b = (t + float(i) * period / 3.0 + f*0.2) * PI * 0.4;
        float s = sin(b);
        float ss = s * 1.0 - 0.5;

        vec2 pos = vec2(p.x*cos(b+1.0)/2.0, p.y*(sin(b+1.0)/2.0));
        pos *= rot(ss * length(p-vec2(s, cos(b))));
        pos *= rot(0.5 * PI);

        vec3 color;
        if (i == 0) {
        	color = vec3(c1, c2, c3);
        } else if (i==1) {
        	color = vec3(c2, c3, c1);
        } else {
        	color = vec3(c3, c1, c2);
        }

        draw += line(pos, 0.6+f*0.01) * color;
    }

    // invert color
    draw = 1. - draw;

    fragColor = vec4(draw,1.0);
}
