// Phoenix - @P_Malin
// https://www.shadertoy.com/view/4tKBRm
// An audio channel thing
// Just random messing with feedback. Warning, this might make you fall over.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 vUV = fragCoord / iResolution.xy;

    fragColor = vec4(0.0);

    fragColor += textureLod( iChannel0, vUV, 0.0 ) * 1.0;    
    fragColor += textureLod( iChannel0, vUV, 2.0 ) * 0.5;    
    fragColor += textureLod( iChannel0, vUV, 4.0 ) * 0.25;    
    fragColor += textureLod( iChannel0, vUV, 5.0 ) * 0.125;    

    fragColor.rgb = ( 1.0 - exp2( fragColor.rgb * -1000.0) );    
}


float SphereIntersect( vec3 vSphereOrigin, float fSphereRadius, vec3 vRayOrigin, vec3 vRayDir )
{
	vec3 vToOrigin = vSphereOrigin - vRayOrigin;
	float fProjection = dot(vToOrigin, vRayDir);
	vec3 vClosest = vRayOrigin + vRayDir * fProjection;

	vec3 vClosestToOrigin = vClosest - vSphereOrigin;
	float fClosestDist2 = dot(vClosestToOrigin, vClosestToOrigin);

	float fSphereRadius2 = fSphereRadius * fSphereRadius;

	if(fClosestDist2 < fSphereRadius2)
	{
		float fHCL = sqrt(fSphereRadius2 - fClosestDist2);

		float t = fProjection + fHCL;

        return t;
	}

    return -1.0;
}


void mainVR( out vec4 vFragColor, in vec2 vFragCoord, in vec3 fragRayOri, in vec3 fragRayDir )
{
	float fSphereT = SphereIntersect( vec3(0,0,0.0), 4.0, fragRayOri, fragRayDir );
	vec3 vHit = fragRayOri + fragRayDir * fSphereT;  

    vHit *= 0.5;

    vec2 vUV = vHit.xy * 0.5 + 0.5;

    //vUV = clamp( vUV, vec2(0), vec2(1));

    mainImage( vFragColor, vUV * iResolution.xy );
}

// Audio History Buffer

#define iChannelAudio iChannel1
#define iChannelAudioHistory iChannel0  

vec4 SampleAudioRaw( float f, float t )
{   
    vec4 r = vec4( 0.0 );
    if ( t <= 0.0 )
    {
        //f = f * f;
        /*
        if ( f > 0.0 )
        {
			f = sqrt( f );
        }*/

        r = textureLod( iChannelAudio, vec2(f, 0.0), 0.0);


        float a = r.r;

        float shade = a * (0.75 + f * 0.25);

        shade = pow( shade, 10.0 ) * 50.0;

        r.r = shade;        
        r.g = f * 30.0 + iTime * 5.0;
        r.a = a * a;               
    }
    else
    {
    	r = textureLod( iChannelAudioHistory, vec2(f, t), 0.0);
    }

    return r;
}

vec4 SampleAudio( float f, float t, float dt )
{
    vec4 sp = SampleAudioRaw( f, t );

    float gradSampleDist = 0.01;

    float xofs = SampleAudioRaw( f - gradSampleDist, t ).a - SampleAudioRaw( f + gradSampleDist, t ).a;

    vec2 d = vec2( xofs * 5.0, -1.0 ) * dt;

    float speed = sp.a * 5.0 * (3.0 * f + 1.4);
    d = normalize(d) * dt * speed;
    f += d.x;
    t += d.y;

    t -= dt * 0.1;

    //t += -dt * (0.2 + f * f * f * 2.0);

    vec4 result = SampleAudioRaw( f, t );


    float fSpread = 0.005; //* (1.0 - sp.a* sp.a);
    result *= 0.8;
    result += SampleAudioRaw( f - fSpread, t ) * 0.1;
    result += SampleAudioRaw( f + fSpread, t ) * 0.1;

    return result;
}

void mainImage( out vec4 vFragColor, in vec2 vFragCoord )
{   
    ivec2 vTexelCoord = ivec2(vFragCoord);

    float t = vFragCoord.y / iResolution.y;

    vFragColor = SampleAudio( vFragCoord.x / iResolution.x, t, iTimeDelta);
}

