vec4 C(vec4 O, vec2 U, float s, int n)
{
    U /= s;
    O =  vec4( (n +ivec4(0,1,2,3) +  int(U.x) + int(U.y) ) % 4 );
    return O;
}

const int KEY_SPACE  = 32;
void mainImage( out vec4 O, vec2 U )
{
 int n;
 //if (iMouse.z>0.) n=iFrame;
  n= iFrame*int(texelFetch( iChannel0, ivec2(KEY_SPACE,2),0 ).x);
  O= C( O,  U, 3.+ iMouse.y/20.,n)*C( O,  U, 2.+ iMouse.x/20., n);
}
